import React,{useState,useEffect} from 'react'
import Loading from '../basic/loading'
import RecommendCart from './RecommendCart'
import AxiosRequest from '../../axios'


const CartProductRecommendation = (props) => {
  const {cat} = props
  const [status,setStatus] = useState(true)
  const [productRecommendation,setProductRecommendation] = useState([])
  const RenderRecommendation = <div></div>
  console.log(cat)


  const retrieveRecommendation = async(props) => {
    const recProducts =await AxiosRequest.post("/shareme/posts/recommend",{"data":cat})
                      .then((res) => {
                                console.log(res.data)                                
                      })
                      .catch((err) => {console.log(err)})

  }

  if(productRecommendation.length >=1){
    setStatus(false)
  }

  useEffect(() => {
    retrieveRecommendation();
  }, [])




  return (
    <div>
        <p style={{fontSize:"24px"}}>You may like these</p>
        {status ? <Loading/> : <RenderRecommendation/>}
    </div>
  )
}

export default CartProductRecommendation
