import React from "react";
import MenuBar from "./Components/Layout/header";
import Meals from "./Components/Meals/Meals";
import DisplayMeals from "./Components/Meals/AvailableMeals";
import { useState } from "react";

let quant = 0;
const App = () => {
  const [cart,setCart] = useState(0);
  const [visible,setVisible] = useState(false);


  const getData = (a) => {
    quant = a;


  }

  const cartQuantity = () => {
    setCart(quant);
  }


  const handleCart = () => {


    setVisible(!visible);

    if(visible === true){
      quant=0;
      setCart(0);

    }
  }


  return (
    <div align="center">
      <div> 
      <MenuBar props={quant} togCart={handleCart}/>
      <br/>
      <br/>
      <br/>
      <br/>
      {!visible && <Meals sendData={cartQuantity}/>}
      <div>
      <DisplayMeals passQuant={getData} vis={visible}/>
      </div>

      </div>

    </div>
  );
}

export default App;
