import { useState } from "react";
import React from 'react';
import Cart from "./cart";





const MealList = (props) => {

    cosnt [Cart,setCart]  = useState(null);


    const RenderMealList = props.passMealDetails.map((each) => {
        return(
            <div key={each.id} style={{display:"flex",justifyContent:"space-between",alignItems:"center"}} className='mb-4'>
                 <div>
                    <h4>{each.name}</h4>
                    <p>Price: {each.price}</p>
                 </div>

                 <div className='d-flex'>
                    <input class="form-control me-2"  style={{width:"100px",borderRadius:"25px"}} placeholder="Add to Cart"/>
                    <button className='btn btn-dark'>Add</button>
                 </div>
            </div> 
        );
    });

    return(
      <div style={{ borderRadius:"25px",marginTop:"3%",backgroundColor:"lightblue",padding:"2%"}} className='container col-md-10'>
           <div align='center' style={{padding:"2%"}}>
            <h1  className='display-5'>Meal List</h1></div>
            {RenderMealList}
      </div>
  );
};


export default MealList;
