import React,{useState,useEffect} from 'react';
import { useLocation } from 'react-router';
const UpdatePost = (props) => {

    const [formData,setFormData] = useState([]);

    useEffect(() => {

    const getFormData = async() => {
      const a = await fetch("http://localhost:8000/shareme/posts/1")
      const b = await a.json()
      setFormData(b)
    }
    getFormData()
    }, []);

    const {title,data,content} = formData

    return(
        <div className='container col-md-6 mt-4'>

            <div className='mb-4'>
                <h1 className='display-4'>Update Post</h1>
            </div>

            <div>
            <div class="mb-3 mt-3">
            <label class="form-label lead">Title</label>
            <input type="text" defaultValue={title} class="form-control" placeholder="Enter the title" onChange={(e) => {setFormData(formDatatitle=e.target.value)}}/>
            </div>

            <div class="mb-3">
            <label class="form-label lead">Content</label>
            <textarea class="form-control"  defaultValue={content} rows="3" placeholder="Enter the content" onChange={(e) => {setFormData(content=e.target.value)}}/>
            </div>

            <div class="mb-3">
            <label class="form-label lead">Data</label>
            <textarea class="form-control" defaultValue={data} rows="3" placeholder="Enter the data" onChange={(e) => {setFormData(data=e.target.value)}}/>
            </div>

            </div>

        </div>
    );
};

export default UpdatePost;
