import React from 'react'
import { useState,useEffect } from 'react'
import {MovieAPIKey} from '../../API/MovieAPIKey'
import MovieAPI from '../../API/MovieAPI'
import MovieCard from './MovieCard'



const MovieList = (props) => {
    const [movieListArray,setMovieListArray] = useState([]);

    useEffect(() => {
        const getMoviesList = async() => {
            const search =""
            console.log(search);
            const movieList = await MovieAPI.get(`?apikey=${MovieAPIKey}&type=movie&s=${search}`);
            const movieScanResults = movieList.data.Search
            setMovieListArray(movieScanResults);

            movieScanResults.map((each) => {
                movieListArray.push(each);
                setMovieListArray(movieListArray);
            })
        }
        getMoviesList();
        console.log(movieListArray);
    }, []);

    const RenderMovies=movieListArray.map((each) => {
            return(
                <div style={{margin:"10%"}}><MovieCard movieData={each}/></div>
            );
    })
    console.log(movieListArray);


    return (
        <div style={{margin:"4%",maxWidth:"100%","display":"grid","gridTemplateColumns": "repeat(auto-fit, minmax(300px, 1fr))"}}>
           {RenderMovies}
        </div>
    )
}

export default MovieList;
