import React,{useReducer } from "react";



const reducer = (state,action) => {
  switch(action.type){
    case "count":
      return{counter: state.counter+1, showText: state.showText};
    case "toggleShowText1":
      return{counter: state.counter, showText:"odd" };
    case "toggleShowText2":
      return{counter: state.counter, showText:"even" };
    default:
      return null;
  }
}


const ReducerApp = () => {
 const [state, dispatch] = useReducer(reducer,{counter:0,showText:"0"});




 return(
    <div align="center">
    <div>

       <h3> {state.counter}</h3>
       <button onClick={() => {
         dispatch({type:"count"});

         if (state.showText ==="odd")
          dispatch({type:"toggleShowText2"});
         else if(state.showText === "0"){
          dispatch({type:"toggleShowText1"});
         }
         else{
          dispatch({type:"toggleShowText1"});
         } 


       }}>increment</button> 
    </div>
    {state.showText === "even" && <p>An even number</p>} 
    {state.showText === "odd" && <p>An odd number</p>} 
    {state.showText === "0" && <p>Lets Start Counting</p>} 
    </div>
  );
}
export default ReducerApp;
