import React, { useState,useEffect } from 'react';
import { useLocation,useNavigate } from 'react-router';
import { Link } from 'react-router-dom';


const Details = () => {

    const navigate = useNavigate();
    const location = useLocation();

    const postID = location.state.key.id;
    const [postDetails,setPostDetails] = useState([]);


    const handleDelete = () => {
        navigate("/posts")
    }


    const retrievePostDetails = async() => {

        const fetchDetails =await  fetch(`http://localhost:8000/shareme/posts/${postID}`)
        const cleanFetchDetails = await fetchDetails.json();
        setPostDetails(cleanFetchDetails);
    }

    useEffect(() => {
        retrievePostDetails();
    }, []);

    return(  
    <div className='container' align="center">
    <div className='col-md-8' style={{textAlign:"left",margin:"3%"}}>
        <div>

        <i className="red trash icon" data-bs-toggle="modal" data-bs-target="#exampleModal"></i>

        <div className="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
            <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Confirm Post Deletion</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
               Are you sure you want to delete this Post
            </div>
            <div className="modal-footer">

                <button type="button" className="btn btn-dark" data-bs-dismiss="modal">Close</button>
                <button type="button" className="btn btn-danger"  onClick={handleDelete} data-bs-dismiss="modal">Confirm Delete</button>
            </div>
            </div>
        </div>
        </div>

        <Link to="update"><i className="red edit icon"></i></Link>


        <h1 className="">{postDetails.title}</h1>

        </div>
        <div style={{margin:"3%"}}>
        <h3>{postDetails.content}</h3>
        </div>

        <div style={{margin:"3%",fontSize:"100%"}}>
        <p>{postDetails.data}</p>
        </div>


    </div>
    </div>
  );

};

export default Details;
