import React,{ useState, useEffect } from 'react'
import Head from './Components/Header';
import { v4 as uuid } from 'uuid';
import Contact from './Components/AddContact';
import ContactList
 from './Components/ContactList';

import { BrowserRouter as Router,Switch,Link,Rou ,Route } from 'react-router-dom';

const App = () => {
  const [contacts,setContacts] = useState([]);
  const LOCAL_STORAGE_KEY = "contacts";

  const addContactHandler = (param) => {


    if(param.name !== "" && param.location!=="" && param.place!==""){
      param.id = uuid();
      setContacts([...contacts,{param}]);

    }
    else{
      alert("Bad Credentials");
    }
  };



  const removeContacthandler = (key) => {


    const newContactList = contacts.filter((i) => {
      return i.param.id !== key;
    });

    setContacts(newContactList);

  }





  useEffect( () => {
    const getAppData = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY, JSON.stringify(contacts)));
    if(getAppData){
      setContacts(getAppData);
    }
  },[]);



  useEffect( () => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(contacts));
  },[contacts]);


  return(
        <div className="ui container">
          {/* <Head/>
          <Contact handlerData={addContactHandler}/>
          <ContactList contacts={contacts} passKey={removeContacthandler}/> */}
          <Router>
          <Head/>
          <Routes>
          <Route path="/" component={ContactList}/>
          <Route path="/add" component={Contact}/>

          </Routes>

          </Router>
        </div>
    );
}

export default App;
