import React from 'react'
import { useState,useEffect } from 'react';
import { Link } from 'react-router-dom';
import AxiosRequest from '../../../axios';
import { useLocation } from 'react-router';

const ReviewMangement = () => {
    const location = useLocation()
    const [comments,setComments] = useState([]); 
    const [postComment,setPostComment] = useState("");
    const key = parseInt(location.pathname.slice(15,))
    const retriveComments = async() => {
        const fetchComments = await AxiosRequest.get(`/shareme/posts/${key}/comments`)
            .then(res => {
                setComments(res.data);
            })
            .catch(err => console.log(err))
    }

    useEffect(() => {    
        retriveComments();
    }, []);



    const RenderComments = comments.map((each) => {
        return(
            <div key={each.id} style={{backgroundColor:"white",borderRadius:"25px"}}>
                <Link to={`/shareme/profile/${each.user}`}><p>{each.user} @{each.username} </p></Link>
                <p className='lead'>{each.content}</p>
                <br/>
            </div>
        );
    })



    const handleComment = async() => {
        const commentRequest =await AxiosRequest.post(`/shareme/posts/${key}/comments`,{content:postComment})
                            .then(res => console.log(res))
                            .catch(err => console.log(err))
        retriveComments();
    }

    return(
        <div>
        <h5 className='display-6'>Reviews</h5>
        <div>
            <input placeholder='   comment' onChange={(e) => setPostComment(e.target.value)} style={{borderRadius:"15px",height:"35px",width:"30rem"}}></input>
            <button className='btn btn-dark' onClick={handleComment}>Comment</button>
           {RenderComments}
        </div>
        </div>
    );
}

export default ReviewMangement;
