  //     </div>
  //   );
  // }




  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {/* {CounterToggle && <CounterBoard/>} */}
      <div className='d-flex justify-center gap-1' style={{marginTop:"4%"}}>
      <button className='btn btn-dark' onClick={() => {Dispatch({type:"increase"})}}>Increase</button>
      <button className='btn btn-dark' onClick={() => {Dispatch({type:"increase",amount:5})}}>Increase5</button>
      <button className='btn btn-dark' onClick={() => {Dispatch({type:"increase",amount:10})}}>Increase10</button>
      <button className='btn btn-dark' onClick={() => {if(CounterData>0){Dispatch({type:"decrease"})}}}>Decrease</button>
      <button className='btn btn-dark' onClick={() => {Dispatch({type:"toggle"})}}>Operate</button>
      </div>
    </main>
  );
};

export default ReduxCounter;
