import React from 'react'
import { useState,useEffect } from 'react';

const Home = () => {

    const [postData,setPostData] = useState([]);

    const GetAPIData = async() => {
        const rawData = await fetch("http://localhost:8000/shareme/posts");
        const jsonData = await rawData.json()
        jsonData.map((each) => {
            postData.push(each)
            return null
        })
        setPostData(postData);
        console.log(postData)
    }

    const RenderData = postData.map((each) => {
        return(
            <div>
                <h1>{each.title}</h1>
            </div>
        );

    })





    useEffect(() => {
      GetAPIData();   

    });


    return (
        <div align="center" className='container col-md-6'>
            {RenderData}
        </div>
    )
}

export default Home;
