import React,{useState,useEffect} from 'react'
import { useLocation, useNavigate } from 'react-router';
import AxiosRequest from '../../axios';
import { DecodeToken } from '../token_decode';
import { Link } from 'react-router-dom';
import { Navigate } from 'react-router';

const Profile = () => {
  const navigation = useNavigate()
  const location = useLocation();
  let key =window.location.pathname.slice(17,)
  const {user_id} = DecodeToken(localStorage.getItem("access_token"))
  const [followData,setFollowData] = useState({following:[],followers:[]})





  const [profile,setProfile] = useState({about:"",id:"",adress:"",birthdate:"",followers:"",following:"",hobby:"",name:"",location:"",status:"",user:"",work:"",image:""});


  const HandleUserNavigation = (idd) => {
      console.log(idd);
      navigation(`/shareme/profile/${idd}`);
      retrieveProfileDetails(idd);
  }


  const retrieveProfileDetails = async(idd) => {
    if(idd){
      key =idd
    }

    const details =await AxiosRequest.get(`/accounts/profile/${key}`)
                    .then((res) => {

                      const {image,about,id,adress,birthdate,followers,following,hobby,name,location,status,user,work} = res.data.ser_data
                      setProfile({about:about,id:id,adress:adress,birthdate:birthdate,followers:followers,following:following,hobby:hobby,name:name,location:location,status:status,user:user,work:work,image:image});
                      setFollowData({following:res.data.fg,followers:res.data.fr})

                    })
                    .catch(err => console.log(err))
  }

  useEffect(() => {
    retrieveProfileDetails()
  },[])


  const handleFollowers = () => {
    const followers = AxiosRequest.put(`/accounts/profile/${key}`,{user_id:user_id,fol_id:key})
                      .then((res) => {retrieveProfileDetails()})
                      .catch((err) => {console.log(err)})
  }


  const FollowButton = () => {
    if(profile.user != user_id){
      return(<div>
              <button onClick={handleFollowers} className='btn btn-dark'>Follow</button>
              <button onClick={() => {navigation(`/shareme/text/${user_id+"-"+profile.user}`)}} className='btn btn-dark'>Text</button>
            </div>);
    }
    else{
      return(null);
    }  
  }

  const RenderFollowers = followData.followers.map((each) => {
    const k = each[1]
    return(
      <div key={each[1]}>

      <p className='lead' onClick={() => {HandleUserNavigation(k)}}> name: {each[0]}, id: {each[1]}  </p>

      </div>
    );
  })


  const RenderFollowing = followData.following.map((each) => {
    const k = each[1]
    return(
      <div key={each[1]}>
      <p className='lead' onClick={() => {HandleUserNavigation(k)}}> name: {each[0]}, id: {each[1]}  </p>

      </div>
    );
  })


  const text_id = user_id+"-"+profile.user;






  return (
    <div className='container row mt-5 ml-5 mb-5'>
      <div className='col-md-4'>
      <img  class="img-fluid" style={{width:"200px",height:"200px"}} src={`http://localhost:8000${profile.image}`}></img>
      <p className='lead mt-3'>Adress: {profile.adress}</p>
      </div>

    <div className='mt-4 ml-4 col-md-6'>
      <h1 className='display-6'>User Details: </h1>
      <p className='lead'>About: {profile.about}</p>

      <p className='lead'>Birthdate{profile.birthdate}</p>
      <p className='lead'>Hobby: {profile.hobby}</p>
      <p className='lead'>Location: {profile.location}</p>
      <p className='lead'>Status: {profile.status}</p>
      <p className='lead'>Work: {profile.work}</p>
    </div>



    <div className='mt-4 ml-4 col-md-2'>
      <h1 className='diplay-6'></h1>

      <p className='lead'>Id: {profile.id}</p>
      <p className='lead'>User: {profile.user}</p>
      <p className='lead'>Followers: {profile.followers.length}</p>

      <p className='lead'>Following: {profile.following.length}</p>
      <h1>Followers</h1>
      {RenderFollowers}
      <h1>following</h1>
      {RenderFollowing}



      <FollowButton/>

    </div>

    </div>
  )
}

export default Profile;
