import RecommendProductCard from './RecommendProductCard'

const CartProductRecommendation = ({props}) => {

  const retrieveRecommendation = async(a) => {
    console.log(a)
    const recProducts = await AxiosRequest.post("/shareme/posts/recommend",{"data":a})
                      .then((res) => {
                        setCategory(a)  
                        setProductRecommendation(res.data)               
                      })
                      .catch((err) => {console.log(err)})


  }






  const recProducts = props


  let a = null;

  const RenderProducts = () => {
    if(recProducts.length >=1){

      a = recProducts.map((each) => {

        return(
          <div key={each.id}>
            <RecommendProductCard props={each} />
          </div>
        );
      })

      return(
        <div>{a}</div>
      );

    }
    else{
      return(
        <div><Loading/></div>
      );
    }

  }










  return (
    <div>
        <p style={{fontSize:"24px"}}>You may like these</p>
        <RenderProducts/>
    </div>
  )
}

export default CartProductRecommendation
