import React from 'react'
import { useState } from 'react';

const UpdateNote = (props) => {
    console.log(props.passData);
    const {data,id,topic,writer} = props.passData;

    const detailObject = {
        "topic":"",
        "data":"",
        "writer":"",
        "id":"",
    }
    const [formData,setFormData] = useState(detailObject);


    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(formData);
    }

    const handleClear = (event) => {
        event.preventDefault();
        setFormData(detailObject);


    }


    return (
        <div className="ui main">
        <form style={{margin:"20px",marginLeft:"20%",marginRight:"20%"}}>
            <div align="center" style={{marginBottom:"40px"}}><h2>Update the Note</h2></div>
            <div className="ui form">
            <div className="field" style={{marginBottom:"25px"}}>
                <label>About</label>
                <textarea rows="2" value={topic}  onChange={(event) => {setFormData({...formData,topic:event.target.value})}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>Note</label>
                <textarea value={data}  onChange={(event) => {setFormData({...formData,data:event.target.value})}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>By: </label>
                <textarea rows="1" value={writer} onChange={(event) => {setFormData({...formData,writer:event.target.value})}}></textarea>
            </div>
            </div>
            <button className="ui button black" onClick={handleClear}>Clear</button>
            <button className="ui button black" onClick={handleSubmit}>Submit</button>


        </form>
        </div>
    )
}
export default UpdateNote;
