import { createSlice, configureStore } from '@reduxjs/toolkit'

// const redux = require('redux')

const boardStates = {board:true,days:0}

const counterSlice = createSlice({
    name:"learn_reduxtools",
    boardStates: {board:true,days:0},
    reducers:{
        increment(state){state.days+=1},
        decrement(state){state.days-=1},
        toggle(state){state.board=!state.board},
        increase_amt(state,action){state.days = state.days+action.payload}
    }
})


const ReduxCentralStore = configureStore({
    reducer:{first: counterSlice.reducer} 
});

export const CounterActions = counterSlice.actions
export default ReduxCentralStore;
