import React from 'react'
import card from '../Images/card.png'
import { Link } from 'react-router-dom';
const NoteDetails = (props) => {
    if(props.passDetails){

    const {topic,data,writer} = props.passDetails;

    return (
        <div align="center">
        <div className="ui card" align="left" style={{margin:"15px"}}>
        <div className="image">
            <img src={card} alt=''/>
        </div>
        <div className="content">
            <div className="header">{topic}</div>
            <div className="meta">
            <span className="date">Written By: {writer}</span>
            </div>
            <div className="description">
            {data}
            </div>
        </div>
        <div className="extra content">
            <div style={{display:"flex",direction:"row",justifyContent:"left",alignItems:"center"}}>
            <Link to="/delete"><i className="close icon"> </i></Link>
            <Link to="/edit"><i className="edit icon"> </i></Link>
            </div>

        </div>
        </div>
        </div>
    );}
    else{
        return(
            <div></div>
        );
    }
}

export default NoteDetails;
