import React from 'react'
import { useState,useEffect } from 'react';
import { Link } from 'react-router-dom';
import AxiosRequest from '../../../axios';
import { useLocation } from 'react-router';
import { Axios } from 'axios';
import { DecodeToken } from '../../token_decode';
import LikeCommentUsers from './likecommentusers';

const ReviewMangement = (props) => {





    const location = useLocation()
    const [comments,setComments] = useState([]); 
    const [postComment,setPostComment] = useState("");
    const key = parseInt(location.pathname.slice(15,))



    const retriveComments = async() => {
        const fetchComments = await AxiosRequest.get(`/shareme/posts/${key}/comments`)
            .then(res => {
                setComments(res.data);

                props.props(res.data.length)
            })

            .catch(err => console.log(err))
    }


    const [commentId,setCommentId] =useState(null)
    const [commentData,setCommentData] = useState({})

    const user_id = DecodeToken(localStorage.getItem("access_token")).user_id




    const LikeComments = async(idd) => {
        const comment_like =await AxiosRequest.post(`/shareme/posts/${key}/comments/likes`,{"user_id":user_id,"comment_id":idd})
                            .then((res) => {
                                console.log(res.data)
                                setCommentData({"res":res.data.res,"data":res.data.data});

                                }); 
    }




    const RenderComments = comments.map((each) => {

        let likeIconStyle = "outline"
        each.likes.map((a) => {
            if(a === user_id){
                likeIconStyle = ""
            }

        })


        return(


// <div  class="mt-5 hover:scale-110  py-8 px-8 max-w-sm mx-auto bg-white rounded-xl shadow-lg space-y-2 sm:py-4 sm:flex sm:items-center sm:space-y-0 sm:space-x-6">
// <img onClick={() => {navigation(`/shareme/profile/${user}`)}} className="block mx-auto h-24 rounded-full sm:mx-0 sm:shrink-0" src={`http://localhost:8000${image1}`} alt="Woman's Face"/>
// <div className="text-right space-y-2 sm:text-left ">
//   <div className="space-y-0.5">
//     <p className="text-xl font-semibold text-black "></p>
//     <p className="text-md text-black "></p>
//     <p className="text-md text-black "></p>

//   </div>

// </div>
// </div>


            <div key={each.id} style={{backgroundColor:"white",borderRadius:"10px",margin:"25px",padding:"15px"}}>

                <div className='d-flex' >

                    <p style={{fontSize:"18px"}}>@{each.username}  {each.user} </p>
                    <div className='d-flex' style={{marginLeft:"30px",justifyContent:"center",alignItems:"center"}}>

                    <i class={`large thumbs up ${likeIconStyle} icon`}  onClick={()=>{LikeComments(each.id);}}></i>

                    <p data-bs-toggle="modal" data-bs-target="#commentlikeusersmodal">{each.likes.length}</p> 
                    <LikeCommentUsers props={each.likes}/>
                    </div> 
                </div>
                <p className='lead'>{each.content}</p>
                <br/>
            </div>
        );
    })



    const handleComment = async() => {
        const commentRequest =await AxiosRequest.post(`/shareme/posts/${key}/comments`,{content:postComment})
                            .then(res => console.log(res))
                            .catch(err => console.log(err))
        retriveComments();
        setPostComment("")
    }


    useEffect(() => {    
        retriveComments();
    }, [commentData]);






    return(
        <div>
        <p style={{fontSize:"20px", marginTop:"30px"}}>Reviews</p>
        <div>
            <input placeholder='   comment' value={postComment} onChange={(e) => setPostComment(e.target.value)} style={{borderRadius:"15px",height:"35px",width:"30rem",marginTop:"10px"}}></input>
            <button className='btn btn-dark mt-3' onClick={handleComment}>Comment</button>
           {RenderComments}
        </div>
        </div>
    );
}

export default ReviewMangement;
