import React,{useState,useEffect} from 'react'
import { Link,useLocation } from 'react-router-dom';
import Cart from '../meals/cart';
import Search from './search';

const Header = () => {
    const [checkLocation,setCheckLocation] = useState(false)
    const location = useLocation().pathname.slice(1,6)
    const [loginState,setLoginState] = useState(false)

    useEffect(() => {
        console.log(location+" "+checkLocation)
        if(location === 'meals'){
          setCheckLocation(true)
        }
        else{
            setCheckLocation(false)
        }
    });

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
                <Link to="/" style={{textDecoration: 'none'}}><a className="navbar-brand" href="/#">ShareME</a></Link>

                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">

                    <li className="nav-item">
                    <Link to="/shareme/posts" style={{textDecoration: 'none'}}><a className="nav-link" aria-current="page" href="/#">Posts</a></Link>
                    </li>
                    <li className="nav-item">
                    <Link to="/meals" style={{textDecoration: 'none'}}><a className="nav-link" href="/#">Meals</a></Link>
                    </li>

                </ul>
                <div  style={{marginRight:"3%" ,display:"flex",justifyContent:"flex-start",alignItems:"center",flexDirection:"row"}}>
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">

                {checkLocation ? <Cart/> : <Search/>}

                <div className='d-flex' >
                {loginState &&
                <li className="nav-item">
                    <Link to="/login" style={{textDecoration: 'none'}}><a className="nav-link" href="/#" tabIndex="-1">Login</a></Link>
                </li>}

                {!loginState &&
                <li className="nav-item">
                    <Link to="/logout" style={{textDecoration: 'none'}}><a className="nav-link" href="/#" tabIndex="-1">Logout</a></Link>
                </li>}

                </div>
                </ul>
                </div>
                </div>
            </div>
            </nav>            
        </div>
    )
}

export default Header;
