import React,{useState,useEffect} from 'react'
import AxiosRequest from '../../axios'


const CartDetails = () => {

    const [cartData,setCartData] = useState([])

    const retrieveCart = async() => {
        const cartData = await AxiosRequest.get(`/shareme/cart/1`)
                        .then((res) => {
                            console.log(res.data);
                            setCartData(res.data)
                        })
                        .catch((err) => {console.log(err)})
    }

    console.log(cartData)
    const RenderCartData = cartData.map((each) => {
        console.log(each);
        return(
            <div align="left">
            <div className='col-md-5'>


            <div style={{backgroundColor:"white", borderRadius:"25px",margin:"25px",padding:"25px"}} key={each.id} >
                <p style={{fontSize:"23px",textAlign:"left"}} className='lead'>{each.title}</p>
                <div style={{textAlign:"left"}} className="row">
                <div className='col-md-6'>


                <p style={{fontSize:"16px"}}> Quantity: {each.quantity}</p>
                <p style={{fontSize:"16px"}}>NetPrice:{each.net_price}</p>
                </div>
                <div className='col-md-6'>
                    <p style={{fontSize:"16px"}}>Change Quantity: </p>
                    <div className='d-flex' style={{gap:"20px"}}>
                    <input style={{borderColor:"black",borderWidth:"2px",width:"5rem",borderRadius:"15px"}}></input>
                    <button className='btn btn-dark'>Set</button>
                    </div>
                </div>
                </div>
            </div>


            </div>
            </div>
        );
    })


    useEffect(() => {
      retrieveCart();


    }, [])



    return (
    <div align="center">
        <h1 className='display-6'>Cart Details</h1>
        {RenderCartData}
    </div>
  )
}

export default CartDetails;
