import React from "react";

const meal_details = [
    {name:"Biryani",price:"150",discount:"20%"},
    {name:"Paratha",price:"10",discount:"10%"},
    {name:"Chowmein",price:"30",discount:"15%"},
    {name:"Oats",price:"20",discount:"18%"},
    {name:"Upma",price:"20",discount:"9%"},
    {name:"Kanji",price:"20",discount:"12%"},
    {name:"Halwa",price:"30",discount:"16%"}
] 


const DisplayMeals = () => {
    const MealsList = meal_details.map((each) => {
        console.log(each.name);
        return(
            <div style={{paddingLeft:"5%" ,display:"flex",justifyContent:"space-between",alignItems:"center",flexDirection:"row",gap:"5%"}}>

<div class="ui relaxed divided list">

  <div class="item" style={{display:"flex"}}>
    <i class="coffee icon"></i>
    <div class="content">
      <b>{each.name}</b>
    </div>
  </div>
  <div class="item" style={{display:"flex"}}>
  <i class="rupee sign icon"></i>

    <div class="content">
      {each.price}
    </div>
  </div>
  </div>

  <div style={{display:"flex"}}>
  <i class="caret square left icon"></i>
  <i class="caret square right icon"></i>
  </div>
  </div>



        );
    });

    return(
        <div>
            <div className="ui raised very padded text container segment">
                  {MealsList}
            </div>
        </div>
    );
}

export default DisplayMeals;
