import React from "react";
import ContactCard from "./ContactCard";

const ContactList = (props) => {


    const getKey = (para) =>{
        props.passKey(para);
    }

    const contacts = [{
        name:"madanmohan",
        id:1,
        email:"madanmohan@vrindavan.com",
        location:"Dwadasha Aditya Tila ",
    }];


    const renderContacts = contacts.map((x) => {
        return(
            <ContactCard elem={x} passKey={getKey}/>
        );
    });
    return(
        <div className="ui celled list">

           <br/>
           <br/>
           <div style={{display:"flex", flexDirection:"row", alignContent:"space-evenly", alignItems:"center"}}>
               <h2> Contact List</h2>
               <form action="/add">
               <button className="ui button black right">AddContact</button>
               </form>
           </div>
           <br/>

           {renderContacts}


        </div>
    );


}

export default ContactList;
