import React,{ useState, useEffect } from "react";
import Header from "./components/header";
import NotesList from "./components/pages/NotesList";
import CreateNote from "./components/pages/CreateNote";

import { BrowserRouter as Router,Route,Routes } from "react-router-dom";
import NoteDetails from "./components/pages/NoteDetails";
import DeleteNote from "./pages/DeleteNote";
import UpdateNote from "./pages/UpdateNote";
import Login from "./pages/Login";


const App = () => {

    const [details,setDetails] = useState();
    const [notesListArray,setNotesListArray] = useState([]);

    const DETAIL_KEY = "detail";

    const getNotesList = async() => {
        const noteList = await fetch("http://localhost:8000/api/notes");
        const data = await noteList.json();

        setNotesListArray(data);
    }



    const handleNewNote = async(param) => {        
        const opt = {
            method:"POST",
            headers: {'Accept': 'application/json','Content-Type': 'application/json'},
            body: JSON.stringify(param),
        }
        await fetch('http://localhost:8000/api/notes/new',opt)
        getNotesList();

        // const request = {...param,id:uuid()}
        // const postResponse = await api.post('/notes',request);
        // setNotesListArray([...notesListArray,param]);
        }






    useEffect(() => {
        //db.json
        // const getNotesList = async() => {
        //     const noteList  = await api.get("/notes");       
        //     if(noteList.data){
        //         setNotesListArray(noteList.data);
        //     }
        // }
        getNotesList();
     }, [])





    const handleDetails = (param) => {
        console.log("detail");
        setDetails(param);
    }
    useEffect(() => {
        if(details){
            localStorage.setItem(DETAIL_KEY,JSON.stringify(details));
        }
    }, [handleDetails])
    useEffect(() => {
        const getDetails = JSON.parse(localStorage.getItem(DETAIL_KEY));
        if(getDetails){
            setDetails(getDetails);
        } 
    }, [])




    const handleDelete = async(key) => {
        localStorage.removeItem("detail");
        const opt = {
            method:"DELETE",
            headers:{'Accept': 'application/json','Content-Type': 'application/json'},
        }
        await fetch(`http://localhost:8000/api/notes/${key}/delete`,opt);
        getNotesList()
        setDetails(null);
    }

    const handleUpdate = async(data) => {
        const {key} = data;
        const opt = {
            method:"PUT",
            headers: {'Accept': 'application/json','Content-Type': 'application/json'},
            body: JSON.stringify(data),
        }
        await fetch(`http://localhost:8000/api/notes/${key}/update`,opt);
        getNotesList();
    }




    return(
        <div>

        <Router>
        <Header/>
            <Routes>     
            <Route path="/notes" element={<Login/>}/>
            <Route path="/notes/add" element={<CreateNote passNote={handleNewNote}/>}/>
            <Route path="/notes/list" element={<NotesList  passNotesList={notesListArray}  getDetails={handleDetails}/>}/>
            <Route path="/notes/:id" element={<NoteDetails passDetails={details}/>}/>
            <Route path="/notes/:id/delete" element={<DeleteNote passDeleteDetails={details} getDeleteKey={handleDelete}/>}/>;
            <Route path="/notes/:id/edit" element={<UpdateNote passData={details}  getFormData={handleUpdate}/>}/> 
            </Routes>
        </Router>
        </div>
    );
}

export default App;
