import React,{ useState, useEffect } from "react";
import Header from "./components/header";
import NotesList from "./pages/NotesList";
import CreateNote from "./pages/CreateNote";

import { BrowserRouter as Router,Route,Routes } from "react-router-dom";
import NoteDetails from "./pages/NoteDetails";
import DeleteNote from "./pages/DeleteNote";
import UpdateNote from "./pages/UpdateNote";
import Login from "./pages/Login";


const App = () => {

    const [details,setDetails] = useState();
    const [notesListArray,setNotesListArray] = useState([]);

    const LIST_KEY = "notes";
    const DETAIL_KEY = "detail";
    // const getNotesList = async() => {
    //     const noteList = await fetch("http://localhost:8000/api/notes")
    //     let data = await noteList.json()

    //     setNotesListArray(data);
    // }



    const handleNewNote = async(param) => {        
        console.log(param);
        const opt = {
            method:"POST",
            headers: {'Accept': 'application/json','Content-Type': 'application/json'},
            body: JSON.stringify(param),
        }
        fetch('http://localhost:8000/api/notes/create',opt).then(function (response) {
            console.log(response);
          })
          .catch(function (error) {
            console.log(error);
          });

        getNotesList();




        // const request = {...param,id:uuid()}
        // const postResponse = await api.post('/notes',request);
        // setNotesListArray([...notesListArray,param]);
        }






    useEffect(() => {

        //localstorage
        // const getNotesList = JSON.parse(localStorage.getItem(LIST_KEY));
        // if(getNotesList){
        //  setNotesListArray(getNotesList);
        // }

        //db.json
        // const getNotesList = async() => {
        //     const noteList  = await api.get("/notes");       
        //     if(noteList.data){
        //         setNotesListArray(noteList.data);
        //     }
        // }




        getNotesList();
     }, [])

    useEffect(() => {
        const stringArray = JSON.stringify(notesListArray);
        localStorage.setItem(LIST_KEY,stringArray);
    }, [notesListArray])




    const handleDetails = (param) => {
        setDetails(param);
    }




    useEffect(() => {
        if(details){
            localStorage.setItem(DETAIL_KEY,JSON.stringify(details));
        }
    }, [handleDetails])

    useEffect(() => {
        const getDetails = JSON.parse(localStorage.getItem(DETAIL_KEY));
        if(getDetails){
            setDetails(getDetails);
        } 
    }, [])




    const handleDelete = (key) => {

        const newArray = notesListArray.filter((each) => {
           return each.id !== key;
        })
        setNotesListArray(newArray);
    }




    return(
        <div>

        <Router>
        <Header/>
            <Routes>     
            <Route path="/" element={<Login/>}/>
            <Route path="/add" element={<CreateNote passNote={handleNewNote}/>}/>
            <Route path="/list" element={<NotesList  passNotesList={notesListArray}  getDetails={handleDetails}/>}/>
            <Route path="/details" element={<NoteDetails passDetails={details}/>}/>
            <Route path="/delete" element={<DeleteNote passDeleteDetails={details} getDeleteData={handleDelete}/>}/>;
            <Route path="/edit" element={<UpdateNote passData={details}/>}/> 
            </Routes>
        </Router>
        </div>
    );
}

export default App;
