import React,{useState,useReducer} from 'react'
import ReduxCounter from '../Redux/Counter'



const HomeApp = () => {




  const reducer = (state,action) => {
    if(action.type === "increment"){
      return {count: state.count+1,displayText:state.displayText}
    }  
    if(action.type === "decrement"){
      return {count: state.count-1,displayText:state.displayText}
    }
    if(action.type === "toggletext"){
      return {count: state.count,displayText:!state.displayText}
    }
  }

  const [state,dispatch] = useReducer(reducer, {count:0,displayText:true})


  const ComponentCounter = () => {
    return(

      <div className='col-md-3 mt-10 bg-white' style={{borderRadius:"15px",color:"brown"}}>
      {state.displayText ? <h3 className='display-6'>Odd Page</h3> :  <h3 className='display-5'>Even Page</h3>}
      <h2 className='display-6'>Clicks: {state.count}</h2>
      <div className='d-flex mt-4 gap-10'>
      <button className='btn btn-dark' onClick={() => {dispatch({type:"increment"}); dispatch({type:"toggletext"})}}>Next Page</button>
      <button className='btn btn-dark' onClick={() => { if(state.count>0){dispatch({type:"decrement"}); dispatch({type:"toggletext"})}}}>Back Page</button>
      </div>
      </div>


    );
  }



  return (

  <div className='container'>
    <div className='row' >

        <ComponentCounter/>
        <ReduxCounter/>
        </div>     
  </div>


  )
}

export default HomeApp
