import { useState,useContext,useRef } from "react";
import React from 'react';
import Cart from "./cart";
import { CartContext } from "../../app";



const MealList = (props) => {
    const cartInputRefValue = useRef()
    const {setCartValue,cartValue} = useContext(CartContext)
    let [cartInput,setCartInput] = useState(0);



    console.log(cartInput + " " +cartValue)




    const RenderMealList = props.passMealDetails.map((each) => {

        const a = each.id


        const handleAdd = () => {
            if(cartInput !=0 && cartInput != null){
                console.log(cartInput + " " +cartValue)
                setCartValue(parseInt(cartValue)+parseInt(cartInput))
            }
        }

        const handleChange = (e) => {
            if(e.target.value >= 0 && e.target.value != null){
                setCartInput(parseInt(e.target.value))
                console.log(cartInput + " " +cartValue)
                console.log(document.getElementById(`$({each.id})`).value)

            } 
        }




        return(
        <div style={{display:"flex",justifyContent:"space-between",alignItems:"center",marginBottom:"3%"}}>
            <div >
            <h3>{each.name}</h3>
            <p className="lead">{each.price}</p>
            </div>
            <div className="d-flex" style={{justifyContent:"flex-end"}}>
                <input  className="form-control me-4" defaultValue="0" id={each.id} onChange={handleChange}  style={{width:"30%",borderRadius:"25px"}} placeholder="No of items" type="number"/>
                <button className="btn btn-dark" onClick={handleAdd}>Add</button>
            </div>
        </div>  
        );
    });

    return(
        <div style={{ borderRadius:"25px",marginTop:"3%",backgroundColor:"lightblue",padding:"2%"}} className='container col-md-6'>
           <div style={{padding:"2%",justifyContent:"space-around",display:"flex",height:"60px",alignItems:"center",marginBottom:"3%    "}}>
            <h1  className='display-5'>Meal List</h1>
            <button className="btn btn-danger" onClick={() => {setCartValue(0)}}>Clear Cart</button>
           </div>
           {RenderMealList}
        </div>
    );
};


export default MealList;
