        <p>Quantity: </p>
        <input onChange={(e) => {setQuantity(parseInt(e.target.value))}}/>
        <button className='btn btn-primary' onClick={() => {handleQuantity(quantity)}}>Add to Cart</button>        
    </div>
  )
}

export default Quantity
