import React,{useState} from 'react'



const CartProductRecommendation = (props) => {

  const [status,setStatus] = useState(false)

  console.log(props.recommend)
  if(props.recommend){
    let a = props.recommend
    RenderRecommendation = a.map((each) => {

      return(
        <div> style={{flexDirection:"column",justifyContent:"center",alignItems:"center"}}
          <div className="card" style="width: 18rem;">
            <img src={`http://localhost:8000${each.image}`} class="card-img-top image-flow"  alt="..." />
            <div className="card-body">
              <h5 className="card-title">{each.title}</h5>
              <p className="card-text">{each.price}</p>

          </div>
          </div>
        </div>
      );
    })
  }


  return (
    <div>
        <p style={{fontSize:"24px"}}>You may like these</p>



    </div>
  )
}

export default CartProductRecommendation
