import React,{useState,useEffect,useRef} from 'react'
import AxiosRequest from '../../axios'
import { DecodeToken } from '../token_decode'


const user_id = DecodeToken(localStorage.getItem("access_token")).user_id
const rec = window.location.pathname.slice(16)
const video_id = user_id + "-"+rec

const videoWebSocket = new WebSocket(`ws://localhost:8000/ws/call/${video_id}`)




const Call = () => {
    const videoReference= useRef()

    const [message,setMessage] = useState([])
    const [videStatus,setVideoStatus] = useState(false)

    const [messageData,setMessageData] = useState([])

    videoWebSocket.onopen = () => {console.log("connection established")}
    videoWebSocket.onclose = () => {console.log("disconnected")}

    videoWebSocket.onmessage = (res) => {
        if(res.data){
            console.log(res)

        }
    }

    const handleMessages = () => {
        messageData.push(message)
        console.log(message)
        videoWebSocket.send(JSON.stringify({"data":message,"code":video_id,"type":"send"}))
    }

    const getVideo = () => {
        navigator.mediaDevices.getUserMedia({ video: { width:500,height:350 },audio:true })
          .then((data) => {
            let video = videoRef.current;
            video.srcObject = data;
            if(videStatus == true){
                video.play();
            }
            else{
                video.pause();
            }
          })
          .catch(err => {
            console.error("error:", err);
          });
      };





    return (
    <div className='container mt-5'>
        <p className='text-center' style={{fontSize:"35px"}}>Video Call</p>


        <div className='mr-3 ml-3 d-flex mb-5' style={{gap:"20px"}}>

        <button className='btn btn-dark'>Audio Off</button>
        <button className='btn btn-dark' onClick={setVideoStatus(!videStatus)}>Video Off</button>
        </div>
        <div className='d-flex'>
        <input></input>
        <button className='btn btn-dark'>Join Room</button>
        </div>

        <div className='d-flex mt-5'>
        <input onChange={(e) => {setMessage(e.target.value)}}></input>
        <button className='btn btn-dark' onClick={handleMessages}>Send Message</button>
        </div>
        <video ref={videoReference}></video>

        let localstream = new MediaStream();





    </div>
  )
}

export default Call
