import React, { useState,useEffect } from 'react';
import { useLocation,useNavigate } from 'react-router';
import { Link } from 'react-router-dom';
import Loading from '../basic/loading';
import { PostContext } from '../../app';
import { useContext } from 'react';
import AxiosRequest from '../../axios';


const decodeToken = (token) => {
    var base64Url = token.split('.')[1];
    var base64 = base64Url.replace('-', '+').replace('_', '/');
    return JSON.parse(window.atob(base64));
}




const Details = () => {

    const navigate = useNavigate();
    const location = useLocation();
    const key = parseInt(location.pathname.slice(15,))

    const {postDetails,setPostDetails} = useContext(PostContext)

    const {details,loading} = postDetails

    const [comments,setComments] = useState([]); 
    const [postComment,setPostComment] = useState("");     
    const handleDelete = async() => {
        const a = await AxiosRequest.delete(`/shareme/posts/${key}/delete`)
                    .then((res) => {navigate("/shareme/posts")})
    }


    const retrievePostDetails = async() => {
        const fetchDetails = await fetch(`http://localhost:8000/shareme/posts/${key}`)
        const cleanFetchDetails = await fetchDetails.json();
        setPostDetails({loading:false,details:cleanFetchDetails});
    }



    const retriveComments = async() => {
        const fetchComments = await AxiosRequest.get(`/shareme/posts/${key}/comments`)
            .then(res => {
                setComments(res.data);
            })
            .catch(err => console.log(err))
    }


    let UserOperations = null
    const username = decodeToken(localStorage.getItem("access_token")).username
    console.log(username+" "+postDetails.details.username)
    if(username ==postDetails.details.username){
        UserOperations = <div className='d-flex'>
                                <Link to={{pathname:"update",state:"state"}}>
                                <i className="large red edit icon"></i>
                                </Link>
                                <i className="large red trash icon" data-bs-toggle="modal" data-bs-target="#exampleModal"></i>
                                </div>       
    }






    useEffect(() => {
        retrievePostDetails();
        retriveComments();
    }, []);



















    const RenderComments = comments.map((each) => {

        return(

            <div key={each.id}>
                <Link to={`/shareme/profile/${each.user}`}><p>{each.user} @{each.username} </p></Link>
                <p className='lead'>{each.content}</p>
                <br/>
            </div>
        );
    })



    const handleComment = async() => {
        const commentRequest =await AxiosRequest.post(`/shareme/posts/${key}/comments`,{content:postComment})
                            .then(res => console.log(res))
                            .catch(err => console.log(err))
        retriveComments();
    }

    if(postDetails.loading ===false){

    return(  
    <div className='container' align="center">
    <div className='col-md-8' style={{textAlign:"left",margin:"3%"}}>
        <div>
        {UserOperations}

        <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
            <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Confirm Post Deletion</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
               Are you sure you want to delete this Post
            </div>
            <div className="modal-footer">

                <button type="button" className="btn btn-dark" data-bs-dismiss="modal">Close</button>
                <button type="button" className="btn btn-danger"  onClick={handleDelete} data-bs-dismiss="modal">Confirm Delete</button>
            </div>
            </div>
        </div>
        </div>




        <h1 className="display-4">{postDetails.details.title}</h1>

        </div>
        <div style={{margin:"3%"}}>
        <h3>{postDetails.details.content}</h3>
        </div>

        <div style={{margin:"3%",fontSize:"125%"}}>
        <p>{postDetails.details.data}</p>
        <Link to={`/shareme/profile/${postDetails.details.author}`}><p className='lead'>{postDetails.details.username}</p></Link>
        </div>
    </div>


    <div>
        <h5 className='display-6'>Comments</h5>
        <div>
            <input placeholder='   comment' onChange={(e) => setPostComment(e.target.value)} style={{borderRadius:"15px",height:"35px",width:"30rem"}}></input>
            <button className='btn btn-dark' onClick={handleComment}>Comment</button>
           {RenderComments}

        </div>
    </div>



































    </div>
  );}
  else{
      return(
          <div><Loading/></div>
      )
  }

};

export default Details;
