import React from 'react'
import { useState } from 'react';
import card from '../Images/card.png'
import { Link } from 'react-router-dom';


const getNotesDetails = async() =>{
    const noteKey = window.location.pathname.slice(7)
    const noteDetail = await fetch(`http://localhost:8000/api/notes/${noteKey}`)
    const data = await noteDetail.json()
    setNoteObject(data);
}




const NoteDetails = () => {

    const [noteObject,setNoteObject] =  useState();
    getNotesDetails();

    if(noteObject){
    const {topic,data,author,key} = noteObject;
    return (
        <div align="center">
        <div className="ui card" align="left" style={{margin:"15px"}}>
        <div className="image">
            <img src={card} alt=''/>
        </div>
        <div className="content">
            <div className="header">{topic}</div>

            <div className="meta">
            <span className="date">Written By: {author}</span>
            </div>
            <div className="description">
            {data}
            </div>
        </div>
        <div className="extra content">
            <div style={{display:"flex",direction:"row",justifyContent:"left",alignItems:"center"}}>
            <Link to={`/notes/${key}/delete`}><i className="close icon"> </i></Link>
            <Link to={`/notes/${key}/edit`}><i className="edit icon"> </i></Link>
            </div>

        </div>
        </div>
        </div>
    );}
    else{
        return(
            <div align="center"><h3>No details available currently</h3></div>
        );
    }
}

export default NoteDetails;
