import React,{ useState } from "react";


const Contact = (props) => {
    const state_dt = {
        name:"",
        location:"",
        email:"",
        id:"",
    }


    const [data,setData] = useState(state_dt);




    const submit = (e) => {
        e.preventDefault();

        if(data.name ==="" || data.location ==="" || data.email === ""){
            alert("fill all the credentials");
        }
        else{
            props.handlerData(data);
            setData(state_dt);
        }
    }



    return(
        <div className="ui main">
            <br/>
            <br/>
            <br/>
            <div style={{display:"flex"}}>
               <h1>Contact List</h1>

               <button className="ui button black" href="/add">AddContact</button>
           </div>
            <form className="ui form" onSubmit={submit} >
                <div className="field">
                    <label>Name</label>
                    <input type="text" value={data.name} onChange={(e) => {setData({...data,name: e.target.value})}} placeholder="enter the name"/>
                </div>
                <div className="field">
                    <label>Email</label>
                    <input type="email" value={data.email} onChange={(e) => {setData({...data,email: e.target.value})}}  placeholder="enter a valid email"/>
                </div>
                <div className="field">
                    <label>Location</label>
                    <input type="text" value={data.location} onChange={(e) => {setData({...data,location: e.target.value})}}  placeholder="enter your location"/>
                </div>
                <button className="ui button black mt-2">Enter</button>
            </form>



        </div>
    );
}


export default Contact;
