import React from 'react'
import { useNavigate } from 'react-router';



const Card = (props) => {
    const navigation = useNavigate()
    // const user_id = DecodeToken(localStorage.getItem("access_token")).user_id
    const user_id = 1
    const data = props.props
    return (
      <div  className="mt-5 py-8 px-8 max-w-sm mx-auto bg-white rounded-xl shadow-lg space-y-2 sm:py-4 sm:flex sm:items-center sm:space-y-0 sm:space-x-6">
      <img onClick={() => {navigation(`/shareme/profile/${data[1]}`)}} class="block mx-auto h-24 rounded-full sm:mx-0 sm:shrink-0" src="https://static8.depositphotos.com/1008939/939/i/600/depositphotos_9394698-stock-photo-lonely-man.jpg" alt="Woman's Face"/>
      <div class="text-center space-y-2 sm:text-left">
        <div class="space-y-0.5">
          <p class="text-lg text-black font-semibold">
            {data[0]}
          </p>
          <p class="text-slate-500 font-medium">
            Product Engineer
          </p>
        </div>

        <button onClick={() => {navigation(`/shareme/text/${user_id}-${data[1]}`)}} class="px-4 py-1 text-sm text-purple-600 font-semibold rounded-full border border-purple-200 hover:text-white hover:bg-purple-600 hover:border-transparent focus:outline-none focus:ring-2 focus:ring-purple-600 focus:ring-offset-2">Message</button>

    </div>
    </div>
  )
}

export default Card
