import React, { useEffect, useState,useReducer } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';

const reducer = (state,action)  => {

  switch(action.type){
    case "emailInput":
      return{enteredEmail:action.data,enteredPassword:state.enteredPassword,formIsValid:state.formIsValid}
    case "passwordInput":
      return{enteredEmail:state.enteredEmail,enteredPassword:action.data,formIsValid:state.formIsValid}
    case "formValidity":
      return{enteredEmail:state.enteredEmail,enteredPassword:state.enteredPassword,formIsValid:action.data}
    default:
      return state;
  }
}



const Login = (props) => {

  const [state, dispatch] = useReducer(reducer,{enteredEmail:"",enteredPassword:"",formIsValid:false})




  useEffect(() => {
    const loginTimer = setTimeout(() => {
      dispatch({type:"formValidity",data:state.enteredEmail.includes("@" && "." && "com" && "mail")&& state.enteredPassword.trim().length>7});
    },500);

    return(() =>{
      clearTimeout(loginTimer);
    });
  },[state.enteredEmail,state.enteredPassword,state.formIsValid]);


  const emailChangeHandler = (event) => {
    dispatch({type:"emailInput",data:event.target.value})
  };

  const passwordChangeHandler = (event) => {
    dispatch({type:"passwordInput",data:event.target.value});
  };


  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(state.enteredEmail, state.enteredPassword);
  };


  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={classes.control}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={state.enteredEmail}
            onChange={emailChangeHandler}

          />
        </div>
        <div
          className={classes.control}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={state.enteredPassword}
            onChange={passwordChangeHandler}

          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!state.formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
