import React from 'react'
import { useState,useEffect } from 'react';
import { Link } from 'react-router-dom';
import AxiosRequest from '../../../axios';
import { useLocation } from 'react-router';
import { Axios } from 'axios';
import { decodeToken } from '../../token_decode';


const ReviewMangement = () => {
    const location = useLocation()
    const [comments,setComments] = useState([]); 
    const [postComment,setPostComment] = useState("");
    const key = parseInt(location.pathname.slice(15,))


    const retriveComments = async() => {
        const fetchComments = await AxiosRequest.get(`/shareme/posts/${key}/comments`)
            .then(res => {
                setComments(res.data);
            })
            .catch(err => console.log(err))
    }


    const [commentId,setCommentId] =useState(null)
    const [commentData,setCommentData] = useState({"data":null,"likes":null})

    const user_id = decodeToken(localStorage.getItem("access_token")).user_id


    const LikeComments = async(idd) => {
        const id = commentId
        const user_id = decodeToken(localStorage.getItem("access_token")).user_id
        const comment_like =await AxiosRequest.post(`/shareme/posts/${key}/comments/likes`,{"user_id":user_id,"comment_id":id})
                            .then((res) => {
                                setCommentData({"res":res.data.res,"data":res.data.data,"likes":res.data.likes,"icon":null})


                            })
    }

    const RenderComments = comments.map((each) => {
        let styleIcon =null 
        if(user_id in each.likes){
            styleIcon = ""
        }
        else{
            styleIcon = "outline"
        }

        return(
            <div key={each.id} style={{backgroundColor:"white",borderRadius:"10px",margin:"25px",padding:"15px"}}>

                <div className='d-flex' >

                    <p style={{fontSize:"18px"}}>@{each.username}  {each.user} </p>
                    <div className='d-flex' style={{marginLeft:"30px",justifyContent:"center",alignItems:"center"}}>

                    <i class={`large thumbs up ${styleIcon} icon`}  onClick={()=>{LikeComments(each.id);}}></i>
                    <p>{each.likes.length}</p> 
                    </div> 
                </div>
                <p className='lead'>{each.content}</p>
                <br/>
            </div>
        );
    })



    const handleComment = async() => {
        const commentRequest =await AxiosRequest.post(`/shareme/posts/${key}/comments`,{content:postComment})
                            .then(res => console.log(res))
                            .catch(err => console.log(err))
        retriveComments();
        setPostComment("")
    }


    useEffect(() => {    
        retriveComments();
    }, [commentData]);






    return(
        <div>
        <h5 className='display-6'>Reviews</h5>
        <div>
            <input placeholder='   comment' value={postComment} onChange={(e) => setPostComment(e.target.value)} style={{borderRadius:"15px",height:"35px",width:"30rem"}}></input>
            <button className='btn btn-dark' onClick={handleComment}>Comment</button>
           {RenderComments}
        </div>
        </div>
    );
}

export default ReviewMangement;
