import React,{useEffect,useState} from 'react';
import MealCard from './MealCard';

const MealsHome = () => {

  const [mealDetails,setMealDetails] = useState({loading:true,mealArray:[]});


  const getMeals = async() => {
    const FetchMealDetails = await fetch('http://localhost:8000/khabar/meals')
    const CleanedMealDetails = await FetchMealDetails.json()

    setMealDetails({loading:false,MealArray:CleanedMealDetails})

  }


  useEffect(() => {
    getMeals();
  },[]);



  const RenderMeals = mealDetails.CleanedMealDetails.map((each) => {
      return(
        <div key={each.id}>
          <MealCard passMealDetails={mealDetails}/>
        </div>
      );
  })


  return( 
    <div>
      <RenderMeals/>
      <h1>loading: {mealDetails.loading}</h1>
    </div>
  );
}


export default MealsHome;
