import React,{ useState, useEffect } from "react";
import Header from "./components/header";
import NotesList from "./pages/NotesList";
import CreateNote from "./pages/CreateNote";
import {v4 as uuid} from 'uuid';
import { BrowserRouter as Router,Route,Routes } from "react-router-dom";
import NoteDetails from "./pages/NoteDetails";
import DeleteNote from "./pages/DeleteNote";
import UpdateNote from "./pages/UpdateNote";

const App = () => {

    const [details,setDetails] = useState();


    // const notesList = [
    //     {topic: "fruit", data: "apple", writer: "akshay"},
    //     {topic: "insect", data: "ant", writer: "manoj"},
    //     {topic: "animal", data: "elephant", writer: "gopal"},
    //     {topic: "sport", data: "ball", writer: "madan"},
    //     {topic: "veichle", data: "bus", writer: "mohan"},
    //     {topic: "hobby", data: "bicycle", writer: "jaga"}
    // ]



    const [notesListArray,setNotesListArray] = useState([]);
    const LIST_KEY = "notes";
    const DETAIL_KEY = "detail";



    const handleNewNote = (param) => {        
        param.id = uuid();
        setNotesListArray([...notesListArray,param]);
    }

    useEffect(() => {
        const getNotesList = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
        if(getNotesList){
         setNotesListArray(getNotesList);
        }

     }, [])


    useEffect(() => {
        const stringArray = JSON.stringify(notesListArray);
        localStorage.setItem(LIST_KEY,stringArray);
    }, [notesListArray])







    useEffect(() => {
        console.log(details);
        // localStorage.setItem(DETAIL_KEY,details);
    }, [handleDetails])



    const handleDelete = (key) => {

        const newArray = notesListArray.filter((each) => {
           return each.id !== key;
        })
        setNotesListArray(newArray);


    }


    const handleDetails = (param) => {
        setDetails(param);
    }




    return(
        <div>

        <Router>
        <Header/>
            <Routes>     
            <Route path="/add" element={<CreateNote passNote={handleNewNote}/>}/>
            <Route path="/list" element={<NotesList  passNotesList={notesListArray}  getDetails={handleDetails}/>}/>
            <Route path="/details" element={<NoteDetails passDetails={details}/>}/>
            <Route path="/delete" element={<DeleteNote passDeleteDetails={details} getDeleteData={handleDelete}/>}/>;
            <Route path="/edit" element={<UpdateNote passData={details}/>}/> 
            </Routes>
        </Router>
        </div>
    );
}

export default App;
