import React,{useState} from 'react'
import { DecodeToken } from '../token_decode'
const SaveLater = () => {

    const user_id = DecodeToken(localStorage.getItem("access_token")).user_id
    const [saveLater,setSaveLater] = useState([])

    const RetrieveOrders = async() => {
        const orders_dt = await AxiosRequest.get(`/savelater/${user_id}`)
                        .then((res)=> {
                            console.log(res.data)
                            setSaveLater(res.data)     
                        })
                        .catch((err) => {console.log(err)})
    }


    const RenderContent = saveLater.map((each) => {
        console.log(each)
        return(
          <div style={{backgroundColor:"white", borderRadius:"20px",margin:"20px",padding:"15px"}} key={each.id} >
          <div className='d-flex' style={{justifyContent:"space-between"}}>
          <p style={{fontSize:"23px",textAlign:"left"}} className='lead'>{each.title}</p>

          </div>

          <div style={{textAlign:"left"}} className="row">
          <div className='col-md-6'>


          <p style={{fontSize:"16px"}}> Quantity: {each.quantity}</p>
          <p style={{fontSize:"16px"}}>NetPrice:{each.net_price}</p>
          </div>

          </div>
      </div>
        );
      })



    return (
        <div>{RenderContent}</div>
  )
}

export default SaveLater
