import React, { useEffect } from 'react'
import { useState } from 'react';



const UpdateNote = (props) => {

    useEffect(() => {

        console.log(formData);

    },[detailObject]);



    const detailObject = {
        "topic":"",
        "data":"",
        "writer":"",
        "id":"",
    }

    // const [formData,setFormData] = useState(detailObject);
    // const [formData,setFormData] = useState(detailObject);
    // const [formData,setFormData] = useState(detailObject);
    const [formData,setFormData] = useState(detailObject);





















    const handleSubmit = (event) => {
        event.preventDefault();

    }

    const handleClear = (event) => {
        // setFormData(detailObject);
        event.preventDefault();



    }


    return (
        <div className="ui main">
        <form style={{margin:"20px",marginLeft:"20%",marginRight:"20%"}}>
            <div align="center" style={{marginBottom:"40px"}}><h2>Update the Note</h2></div>
            <div className="ui form">
            <div className="field" style={{marginBottom:"25px"}}>
                <label>About</label>
                <textarea rows="2" value={formData.topic} onChange={(e) => {detailObject.topic=e.target.value;setFormData(detailObject)}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>Note</label>
                <textarea value={formData.data} onChange={(e) => {detailObject.data=e.target.value;setFormData(detailObject)}}></textarea>
            </div>
            <div className="field" style={{marginBottom:"25px"}}>
                <label>By: </label>
                <textarea rows="1" value={formData.writer} onChange={(e) => {detailObject.writer=e.target.value;setFormData(detailObject)}}></textarea>
            </div>
            </div>
            <button className="ui button black" onClick={handleClear}>Clear</button>
            <button className="ui button black" onClick={handleSubmit}>Submit</button>
        </form>
        </div>
    );

}
export default UpdateNote;
