import React from "react";
import { useState } from "react";


const meal_details = [
    {name:"Biryani",price:"150",discount:"20%",quantity:0},
    {name:"Paratha",price:"10",discount:"10%",quantity:0},
    {name:"Chowmein",price:"30",discount:"15%",quantity:0},
    {name:"Oats",price:"20",discount:"18%",quantity:0},
    {name:"Upma",price:"20",discount:"9%",quantity:0},
    {name:"Kanji",price:"20",discount:"12%",quantity:0},
    {name:"Halwa",price:"30",discount:"16%",quantity:0}
] 


const DisplayMeals = () => {
    const [toggleCart,setToggleCart] =useState(true);

    const [quantity,setQuantity] = useState(0);
    const MealsList = meal_details.map((each) => {


    const dec = () => {
        each.quantity-=1;
        setQuantity(each.quantity);
        console.log(each.quantity);
    }

    const inc = () => {

        each.quantity +=1;
        setQuantity(each.quantity);
        console.log(each.quantity);
    }
    return(
        <div style={{marginBottom:"6%" ,paddingLeft:"5%" ,display:"flex",justifyContent:"space-between",alignItems:"center",flexDirection:"row",gap:"5%"}}>

	<div className="ui relaxed divided list">

	  <div className="item" style={{display:"flex"}}>
	    <i className="coffee icon"></i>
	    <div className="content">
	      <b>{each.name}</b>
	    </div>
	  </div>
	  <div className="item" style={{display:"flex"}}>
	  <i className="rupee sign icon"></i>

	    <div className="content">
	      {each.price}
	    </div>
	  </div>
	  </div>

	  <div style={{display:"flex",justifyContent:"center",alignItems:"center",gap:"3%"}}>
	  <h3>{each.quantity}</h3>
	  <div>
	  <button className="ui button" onClick={inc}><i className="caret square up icon"></i></button>

	  <button className="ui button" onClick={dec}><i className="caret square down icon"></i></button>
	  </div>
	  </div>
	  </div>



        );
    });

    const CartList = () => {
        let tot_quantity = 0;
        const CartElements = meal_details.map((elem) => {
            if (elem.quantity >0){
              tot_quantity += elem.quantity
              return(
                <div style={{marginBottom:"6%" ,paddingLeft:"5%" ,display:"flex",justifyContent:"space-between",alignItems:"center",flexDirection:"row",gap:"5%"}}>

                <div className="ui relaxed divided list">

                  <div className="item" style={{display:"flex"}}>
                    <i className="coffee icon"></i>
                    <div className="content">
                      <b>{elem.name}</b>
                    </div>
                  </div>
                  <div className="item" style={{display:"flex"}}>
                  <i className="rupee sign icon"></i>

                    <div className="content">
                      {elem.price}
                    </div>
                  </div>
                  </div>


                  </div>
              );
            }

        })
    }



    return(
        <div>
            <div className="ui raised very padded text container segment">
                  {toggleCart && MealsList}
                  {!toggleCart && CartList}
            </div>
        </div>
    );
}

export default DisplayMeals;
