import React,{useState,useEffect} from 'react'
import { Link,useLocation } from 'react-router-dom';
import Search from './search';
import Logout from './logout';
import { useNavigate } from 'react-router';
import CartIcon from '../Cart/CartIcon';
import { DecodeToken } from '../token_decode';

const Header = () => {

    const [checkLocation,setCheckLocation] = useState(false)
    const location = useLocation().pathname.slice(1,6)
    const [loginState,setLoginState] = useState(false)
    const navigation = useNavigate()
    const user_id = DecodeToken(localStorage.getItem("access_token")).user_id


    useEffect(() => {

        if(location === 'meals'){
          setCheckLocation(true)
        }
        else{
            setCheckLocation(false)
        }
    });


    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <div className="container-fluid">
                <a style={{textDecoration: 'none'}} className="navbar-brand" onClick={() => {navigation("/shareme/profile")}}>ShareME</a>

                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">

                    <li className="nav-item">
                    <Link to="/shareme/posts" style={{textDecoration: 'none'}}><a className="nav-link" aria-current="page" href="/#">Products</a></Link>
                    </li>

                    <li className="nav-item">
                    <Link to="/shareme/posts" style={{textDecoration: 'none'}}><a className="nav-link" aria-current="page" href="/#">Products</a></Link>
                    </li>



                </ul>
                <div  style={{marginRight:"3%" ,display:"flex",justifyContent:"flex-start",alignItems:"center",flexDirection:"row"}}>
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">



                <div className='d-flex' >
                {loginState &&
                <li className="nav-item">
                    <Link to="/login" style={{textDecoration: 'none'}}><a className="nav-link" href="/#" tabIndex="-1">Login</a></Link>
                </li>}

                {!loginState &&
                <li className="nav-item" >
                    <div className='d-flex' style={{gap:"15px"}}>
                    {/* <CartIcon/> */}
                    <Link to={`/shareme/cart/${user_id}`} ><i className='orange big cart arrow down icon'/></Link>
                    <Link to={`/shareme/notifications/${user_id}`} ><i className='orange big bell icon'/></Link>
                    <Link to={`/shareme/orders/${user_id}`} ><i className='orange big truck icon'/></Link>
                    <Link to={`/shareme/sales/${user_id}`} ><i className='orange big dollar sign icon'/></Link>
                    <a className="nav-link" href="/#" data-bs-toggle="modal" data-bs-target="#staticBackdrop">Logout</a>
                    <Logout/>
                    </div>
                </li>}

                </div>
                </ul>
                </div>
                </div>
            </div>
            </nav>            
        </div>
    )
}

export default Header;
