import React from 'react'
import { useState,useEffect } from 'react';
import Card from './card';
import Details from './details';
import Loading from '../basic/loading';
import { Link } from 'react-router-dom';


const Home = () => {

    const [postData,setPostData] = useState({loading:true,posts:[]});

    const GetAPIData = async() => {
        const rawData = await fetch("http://localhost:8000/shareme/posts");
        const jsonData = await rawData.json()
        setPostData({loading:false,posts:jsonData});
    }

    useEffect(() => {
        GetAPIData();   
    },[]);


    const SearchBar = () => {
        return(
            <div style={{display:"flex",alignItems:"center",marginRight:"3%"}}>
                <input class="form-control me-2"  style={{width:"150px"}} type="search" placeholder="Search" aria-label="Search"/>
            </div>
        );
    }




    const RenderPostListData = postData.posts.map((each) => {
        return(
            <div key={each.id} style={{marginLeft:"3%",marginTop:"3%",marginRight:"3%",}}>
                <Card CardViewData={each}/>
            </div>
        );
    })
    if(postData.loading === false){
    return (
        <div className='container mt-3'>
        <div align="left">
            <Link to='/shareme/posts_create'><i className='red big clone icon'></i></Link>

            </div>
        <div align="center" className='container' style={{"display":"grid","gridTemplateColumns": "repeat(auto-fit, minmax(200px, 1fr))"}}>
            {RenderPostListData}
        </div>
        </div>
    )
    }
    else{
        return(
            <div>
                <Loading/>
            </div>
        );
    }
}

export default Home;
