import React,{useState} from 'react'
import { Link,useNavigate } from 'react-router-dom';
import AxiosRequest from '../../axios';


const Signup = () => {
    const navigation = useNavigate();
    const initialRegistrationDetails = Object.freeze({user_name:"",name:"",email:"",password:"",confirm_password:""})
    const [register,setRegister] = useState(initialRegistrationDetails);


    const handleClear = () => {
        setRegister();
    }

    const handleSubmit = () => {
        console.log(register);
        const requestData = JSON.stringify({email:register.email,user_name:register.username,password:register.password})
        AxiosRequest.post('accounts/register/',requestData)
        .then((res) => {
            navigation('/login')
            console.log(res)
            console.log(res.data)
        })
        console.log(register);
    }

    const handleChange = (e) => {
        const feedRegisterForm = {...register, [e.target.name]:e.target.value}
        setRegister(feedRegisterForm)
    }




    return (
        <div className="container col-md-5 mt-4">
        <div align="center">
        <h1>Enter details to register</h1>
        <p className='lead'>Already register <Link to="/login">Click here</Link> to Login</p>
        </div>

        <div className="mb-3 mt-4">
        <div style={{display:"flex",justifyContent:"flex-start",alignItems:"center", marginBottom:"2%"}}>
        <i class="big user icon"></i>
        <p  className="form-label lead">Name</p>
        </div>

        <input type="name" name="name" className="form-control" placeholder="Enter your full name" onChange={handleChange}/>
        </div>
        <div className="mb-3">
        <div style={{display:"flex",justifyContent:"flex-start",alignItems:"center", marginBottom:"2%"}}>
        <i class="big user outline icon"></i>
        <p  className="form-label lead">Username</p>
        </div>
        <input type="username" name="user_name" className="form-control" placeholder="Enter a unique username"  onChange={handleChange}/>
        </div>
        <div className="mb-3">
        <div style={{display:"flex",justifyContent:"flex-start",alignItems:"center", marginBottom:"2%"}}>
        <i class="big envelope icon"></i>
        <p  className="form-label lead">Email</p>
        </div>
        <input type="email" name="email" className="form-control" placeholder="Enter Email Id" onChange={handleChange}/>
        </div>
        <div className="mb-3">

        <div style={{display:"flex",justifyContent:"flex-start",alignItems:"center", marginBottom:"2%"}}>
        <i class="big keyboard icon"></i>
        <p  className="form-label lead">Password</p>
        </div>
        <input type="password" name="password" className="form-control" placeholder="Enter Password" onChange={handleChange}/>
        </div>
        <div className="mb-3">
        <div style={{display:"flex",justifyContent:"flex-start",alignItems:"center", marginBottom:"2%"}}>
        <i class="big keyboard icon"></i>
        <p  className="form-label lead">Confirm Password</p>
        </div>
        <input type="password" name="confirm_password" className="form-control" placeholder="Conform Password" onChange={handleChange}/>
        </div>
        <div className='d-grid gap-2'>
        <button type="button" class="btn btn-dark" onClick={handleSubmit}>Submit</button>
        <button type="button" class="btn btn-danger" onClick={handleClear}>Clear</button>
        </div>
        </div>


    )
}

export default Signup;
