import React,{useState,useEffect} from 'react'
import { DecodeToken } from '../token_decode'
import AxiosRequest from '../../axios'

const CartDetails = () => {

    const user_id = DecodeToken(localStorage.getItem("access_token"));
    const [cartData,setCartData] = useState([])
    const [quantity,setQuantity] = useState();

    const retrieveCart = async() => {
        const cartData = await AxiosRequest.get(`/shareme/cart/${user_id}`)
                        .then((res) => {
                            console.log(res.data);
                            setCartData(res.data)
                        })
                        .catch((err) => {console.log(err)})
    }


    const handleQuantity = async() => {
        const setQuantity = await AxiosRequest.put(`/shareme/cart/${user_id}`)
    }

    console.log(cartData)
    const RenderCartData = cartData.map((each) => {
        console.log(each);
        return(



            <div style={{backgroundColor:"white", borderRadius:"25px",margin:"25px",padding:"25px"}} key={each.id} >
                <p style={{fontSize:"23px",textAlign:"left"}} className='lead'>{each.title}</p>
                <div style={{textAlign:"left"}} className="row">
                <div className='col-md-6'>


                <p style={{fontSize:"16px"}}> Quantity: {each.quantity}</p>
                <p style={{fontSize:"16px"}}>NetPrice:{each.net_price}</p>
                </div>
                <div className='col-md-6'>
                    <p style={{fontSize:"16px"}}>Change Quantity: </p>
                    <div className='d-flex' style={{gap:"20px"}}>
                    <input style={{borderColor:"black",borderWidth:"2px",width:"5rem",borderRadius:"15px"}} onChange={(e) => {setQuantity(parseInt(e.target.value))}}></input>
                    <button className='btn btn-dark' onClick={handleQuantity}>Set</button>
                    </div>
                </div>
                </div>
            </div>



        );
    })


    useEffect(() => {
      retrieveCart();


    }, [])



    return (
    <div align="center">
        <h1 className='display-6'>Cart Details</h1>
        <div className='container row'>
        <div className='col-md-5'>
        {RenderCartData}
        </div>
        <div className='col-md-3'></div>

        <div className='col-md-4' style={{textAlign:"right"}}>
            <p style={{fontSize:"30px"}}>Recommended Products</p>
        </div>

        </div>
    </div>
  )
}

export default CartDetails;
