import React from "react";

const meal_details = [
    {name:"Biryani",price:"150",discount:"20%"},
    {name:"Paratha",price:"10",discount:"10%"},
    {name:"Chowmein",price:"30",discount:"15%"},
    {name:"Oats",price:"20",discount:"18%"},
    {name:"Upma",price:"20",discount:"9%"},
    {name:"Kanji",price:"20",discount:"12%"},
    {name:"Halwa",price:"30",discount:"16%"}
] 


const DisplayMeals = () => {
    const MealsList = meal_details.map((each) => {
        console.log(each.name);
        return(
            <div style={{paddingLeft:"5%" ,display:"flex",justifyContent:"flex-start",alignItems:"center",flexDirection:"row",gap:"5%"}}>

                <h2 className="ui header"></h2>
                <h4>Rs. {each.price}</h4>
                <div class="ui list">
  <div class="item">
    <i class="users icon"></i>
    <div class="content">
      List of Meals
    </div>
  </div>
  <div class="item">
    <i class="coffee icon"></i>
    <div class="content">
      {each.name}
    </div>
  </div>
  <div class="item">
    <i class="mail icon"></i>
    <div class="content">
     Rs. {each.price}
    </div>
  </div>
  <div class="item">
    <i class="linkify icon"></i>
    <div class="content">
      <a href="http://www.semantic-ui.com">semantic-ui.com</a>
    </div>
  </div>
</div>



                {/* <div style={{display:"flex",gap:"10%",justifyContent:"flex-end",alignItems:"center",flexDirection:"row"}}>

                <button className="ui button black right"><i class="angle double right icon"></i></button>
                    <h2>0</h2>
                <button className="ui button black right"><i class="angle double right icon"></i></button>
                <br/>
                <br/>

                </div> */}


            </div>
        );
    });

    return(
        <div>
            <div className="ui raised very padded text container segment">
            {MealsList}
            </div>
        </div>
    );
}

export default DisplayMeals;
