import "./form.css"
import { useState } from "react"

const Form = (props) =>{

    const [username,setUsername] = useState("");
    const [age,setAge] = useState("");

    const handleClear = (event) => {
        event.preventDefault();
        setAge("");
        setUsername("");


    }

    const handleSubmit = (event) => {
        event.preventDefault();
        if(username.length>0 && age>0 && age<150){
        let data = {"username":username.toString(),"age":age.toString()}
        props.getData(data);

        }

    }

    const handleUsername = (event) => {
        setUsername(event.target.value); 

    }

    const handleAge = (event) => {
        setAge(event.target.value);    
    }


    return(
        <div>
            <a className="heading"><h1 class="display-5">Welcome to my first hands on ReactJS Project</h1></a>
            <form>
            <div class="mb-3">
            <label class="form-label">Username</label>
            <input type="text" onChange={handleUsername} class="form-control"  placeholder="Enter your username"/>
            </div>
            <div class="mb-3">
            <label class="form-label">Age</label>
            <input type="number" onChange={handleAge} class="form-control" placeholder="Enter your age"/>
            </div>
            <div className="btnflex">

            <button class="btn btn-dark ml-4" type="submit" onClick={handleClear}>Clear</button>
            <button class="btn btn-dark mr-4" type="submit" onClick={handleSubmit}>Submit</button>
            </div>
            </form>
        </div>
    );
}

export default Form;
