import React,{ useState, useEffect } from "react";
import Header from "./components/header";
import NotesList from "./pages/NotesList";
import CreateNote from "./pages/CreateNote";


const App = () => {

    const notesList = [
        {topic: "fruit", data: "apple", writer: "fads"},
        {topic: "insect", data: "ant", writer: "fads"},
        {topic: "sada", data: "elephant", writer: "fads"},
        {topic: "sada", data: "ball", writer: "fads"},
        {topic: "sada", data: "bus", writer: "fads"},
        {topic: "sada", data: "bicycle", writer: "fads"}
    ]







    const [notesListArray,setNotesListArray] = useState([])
    const LOCAL_STORAGE_KEY = "notes";



    const handleNewNote = (param) => {        
        setNotesListArray([...notesListArray,{param}]);
    }

    useEffect(() => {
        const stringArray = JSON.stringify(notesListArray)
        localStorage.setItem(LOCAL_STORAGE_KEY,stringArray);

    }, [notesListArray])

    return(
        <div >


            <Header/>

            <NotesList passNotesList={notesList}/>
            <CreateNote passNote={handleNewNote}/>
        </div>
    );

}

export default App;
