import React from 'react';
import reactJS from '../../Images/reactJS.png'
import { useNavigate } from 'react-router';


const Card = (props) => {

    const navigation = useNavigate();


    const {title,id,price,image} = props.CardViewData



    return(
    <div>
        <div className="card shadow p-2 mb-5 bg-body rounded" style={{width:"13rem"}} >
        <img src={`http://localhost:8000${image}`} className="card-img-top" alt="..." onClick={(e) => {navigation(`${id}`)}}/>
        <div className="card-body" align="left">
            <p className="card-title">{title.substr(0,40)}</p>
            <p>Rs: {price}</p>
        </div>
        </div>
    </div>
  );
};

export default Card;



<div class="max-w-sm rounded overflow-hidden shadow-lg">
  <img class="w-full" src="/img/card-top.jpg" alt="Sunset in the mountains"/>
  <div class="px-6 py-4">
    <div class="font-bold text-xl mb-2">The Coldest Sunset</div>
    <p class="text-gray-700 text-base">
      Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatibus quia, nulla! Maiores et perferendis eaque, exercitationem praesentium nihil.
    </p>
  </div>
  <div class="px-6 pt-4 pb-2">
    <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">#photography</span>
    <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">#travel</span>
    <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">Rs: {price}</span>
  </div>
</div>
