import React from "react";

const meal_details = [
    {name:"biryani",price:"150",discount:"20%"},
    {name:"paratha",price:"10",discount:"10%"},
    {name:"chowmein",price:"30",discount:"15%"},
    {name:"oats",price:"20",discount:"18%"},
    {name:"upma",price:"20",discount:"9%"},
    {name:"kanji",price:"20",discount:"12%"},
    {name:"halwa",price:"30",discount:"16%"}
] 


const DisplayMeals = () => {
    const MealsList = meal_details.map((each) => {
        console.log(each.name);
        return(
            <div className="ui raised very padded text container segment">
                <h2 className="ui header">{each.name}</h2>
                <p>Discount: {each.discount}</p>
                <p>Price: {each.price}</p>
            </div>
        );
    });

    return(
        <div>
            <div className="ui raised very padded text container segment"></div>
            {MealsList}
        </div>
    );
}

export default DisplayMeals;
