import React from 'react'
import { useState,useEffect } from 'react';
import Card from '../posts/card';


const Home = () => {

    const [postData,setPostData] = useState([]);

    const GetAPIData = async() => {
        const rawData = await fetch("http://localhost:8000/shareme/posts");
        const jsonData = await rawData.json()
        setPostData(jsonData);
    }

    useEffect(() => {
        GetAPIData();   
    },[]);


    const RenderData = postData.map((each) => {

        return(
            <div key={each.id} style={{gridTemplateColumns:"200px repeat(auto-fill, 100px) 300px"}}>
                <Card CardViewData={postData}/>
            </div>
        );

    })









    return (
        <div align="center" className='container col-md-6'>
            {RenderData}
        </div>
    )
}

export default Home;
