import React, { useState,useEffect } from 'react';
import { useLocation,useNavigate } from 'react-router';
import { Link } from 'react-router-dom';
import Loading from '../basic/loading';
import { PostContext } from '../../app';
import { useContext } from 'react';
const Details = () => {

    const navigate = useNavigate();
    const location = useLocation();
    const key = parseInt(location.pathname.slice(15,))

    const {postDetails,setPostDetails} = useContext(PostContext)
    const {details,loading} = postDetails




    const handleDelete = () => {
        navigate("/shareme/posts")
    }




    if(postDetails.loading ===false){

    return(  
    <div className='container' align="center">
    <div className='col-md-8' style={{textAlign:"left",margin:"3%"}}>
        <div>

        <i className="large red trash icon" data-bs-toggle="modal" data-bs-target="#exampleModal"></i>

        <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
            <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Confirm Post Deletion</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
               Are you sure you want to delete this Post
            </div>
            <div className="modal-footer">

                <button type="button" className="btn btn-dark" data-bs-dismiss="modal">Close</button>
                <button type="button" className="btn btn-danger"  onClick={handleDelete} data-bs-dismiss="modal">Confirm Delete</button>
            </div>
            </div>
        </div>
        </div>

        <Link to={{pathname:"update",state:"state"}}>
            <i className="large red edit icon"></i>
        </Link>

        <h1 className="display-4">{postDetails.details.title}</h1>

        </div>
        <div style={{margin:"3%"}}>
        <h3>{postDetails.details.content}</h3>
        </div>

        <div style={{margin:"3%",fontSize:"125%"}}>
        <p>{postDetails.details.data}</p>
        </div>
    </div>
    </div>
  );}
  else{
      return(
          <div><Loading/></div>
      )
  }

};

export default Details;
